                    if ('\n' == out.charAt(out.length - 1))
                        out += this.tabifier.getTabs();
                    if (nl && '/' != tag.charAt(1))
                        this.tabifier.cleanlevel++;

                    out += tag;

                    if (tag.match(this.tabifier.lineAfter) || tag.match(this.tabifier.newLevel)) {
                        out = out.replace(/ *$/, '');
                        //out += '\n';
                    }

